UPDATE courses SET
    course_description = '<p>The Graduate Certificate in Pollution Impact Assessment program is designed to equip graduates with specialist training in assessing the impact of pollution and environmental contaminants and to provide high quality industry ready graduates to meet end user needs.</p><p>The Graduate Certificate in Pollution Impact Assessment courses are focused postgraduate courses integrating required disciplines to provide graduates with capabilities for assessing environmental contaminants and determining the impact of contaminants on the environment.</p>',
    onshore_tuition_fee = '',
    offshore_tuition_fee = '',
