UPDATE courses SET
    course_description = '<p>The Master of Environmental Law is a professionally-focussed and interdisciplinary program that will expose students to new perspectives and deep analyses of environmental law issues, ensuring high quality teaching and learning experiences for students.Graduates of this program will be equipped with advanced knowledge and skills, positioning them to engage in further education, research and professional development in Australia and across the world.</p>',
    onshore_tuition_fee = '',
