    offshore_tuition_fee = '',
    entry_requirements = '<div class="component admission-criteria"> <p style="font-weight: bold;"> Admission criteria </p> <p> Before you begin your application to study as a domestic student, check that you meet the requirements listed below. </p> <div class="admission-criteria__list"> <div class="admission-criteria__item tick"> <p>AQF Level 7 (Bachelor degree) or above from an Australia University</p> <p><strong>OR</strong></p> <p>An equivalent overseas higher education qualification</p> </div> <div class="admission-criteria__item"> The below is an <strong>alternative option.</strong> <br/> A combination of formal (below AQF7) or non-formal qualifications and three years of relevant professional or personal experience includes those who are currently working in education-related fields and teachers holding alternative qualifications. </div> </div> <p class="admission-links"> <a class="btn-tertiary" href="https://cdn.intelligencebank.com/au/share/RyzZ/glewO/XrWEA/original/CIS_GCANS25" rel="noopener noreferrer" target="_blank" title="View full course admission information | Torrens University">View full course admission information</a> </p> </div>',
