    offshore_tuition_fee = '',
    entry_requirements = '<div class="component admission-criteria"> <p style="font-weight: bold;"> Admission criteria </p> <p> Before you begin your application to study as a domestic student, check that you meet the requirements listed below. </p> <div class="admission-criteria__list"> <div class="admission-criteria__item tick"> <p>AQF Level 7 (Bachelor degree) or above.</p> <p><strong>OR</strong> </p> <p>Combination of formal (below AQF7) or non-formal qualifications and seven years of relevant experience in a health professional role.</p> </div> <div class="admission-criteria__item"> Successful completion of the Graduate Certificate will allow automatic entry to the Graduate Diploma or Master of Public Health with 100% credit. </div> </div> <p class="admission-links"> <a class="btn-tertiary" href="https://cdn.intelligencebank.com/au/share/RyzZ/glewO/VNDjb/original/CIS_GCPH21" rel="noopener noreferrer" target="_blank" title="View full course admission information | Torrens University">View full course admission information</a> </p> </div>',
