UPDATE courses SET
    course_description = '<div class="course_heading_description">\n<div class="text">\nThis double degree is designed for students whose interests span the Arts and Business.\n\t\t\t\t\t\t\t\t\t\t</p>\n</div>\n</div>\n</div>',
