    course_description = '<div class="cmp-course__overview__two-column">\n<div class="cmp-course__overview__two-column--container__wrapper">\n<div class="cmp-course__overview__two-column--left">\n<!---Title-->\n<div class="component component--tile">\n<div class="component component--title">\n<p style="font-weight: bold;" class="title__text">Raising the global bar</p>\n</div>\n</div>\n<!--- RTE component -->\n<div class="component component--wysiwyg">\n                    The Master of Construction Law (MCL) program is designed to meet the needs of construction professionals and construction lawyers from Australia and overseas. Working with lecturers who are leaders in their fields, students will have a unique opportunity to gain an advanced understanding of construction law and its interaction with the commerce and practice of the industry via online and face-to-face delivery modes.\n\nNote: The law component of this program is held at the Parramatta South campus.\n                </div>\n</div>\n<div class="cmp-course__overview__two-column--right">\n<!-- Image-->\n<div class="image parbase">\n<div class="image-component">\n<a class="image-link" target="blank">\n\n</a>\n</div>\n</div>\n</div>\n</div>\n</div>',
