UPDATE courses SET
    course_description = '<div class="cmp-course__overview__two-column">\n<div class="cmp-course__overview__two-column--container__wrapper">\n<div class="cmp-course__overview__two-column--left">\n<!---Title-->\n<div class="component component--tile">\n<div class="component component--title">\n<p style="font-weight: bold;" class="title__text">Interpreting at Western</p>\n</div>\n</div>\n<!--- RTE component -->\n<div class="component component--wysiwyg">\n                    The Graduate Diploma in Interpreting provides suitably qualified graduates with training in interpreting leading to eligibility to sit for certification by the National Accreditation Authority for Translators and Interpreters (NAATI) at the Certified Interpreter level. Graduates of the Graduate Diploma in Interpreting can articulate into the Master of Arts Translation and Interpreting Studies.\n\nAll practical components are informed by the theories of Interpreting, Translation, related disciplines, and the results of current research.\n\nSubject to demand, the following languages are available: Arabic, Japanese, Mandarin, Korean, Vietnamese and Spanish.\n                </div>\n</div>\n<div class="cmp-course__overview__two-column--right">\n<!-- Image-->\n<div class="image parbase">\n<div class="image-component">\n<a class="image-link" target="blank">\n\n</a>\n</div>\n</div>\n</div>\n</div>\n</div>',
