UPDATE courses SET
    course_description = '<div class="cmp-course__overview__two-column">\n<div class="cmp-course__overview__two-column--container__wrapper">\n<div class="cmp-course__overview__two-column--left">\n<!---Title-->\n<div class="component component--tile">\n<div class="component component--title">\n<p style="font-weight: bold;" class="title__text">Engineering Your Future</p>\n</div>\n</div>\n<!--- RTE component -->\n<div class="component component--wysiwyg">\n                    The Bachelor of Engineering Advanced (Honours) is a four-year honours degree program. The program has been designed to meet Engineers Australia professional accreditation requirements. Students have the opportunity to focus on a discipline area by selecting a Major in Advanced Manufacturing, Civil, Construction, Electrical, Materials, Mechanical, Robotic &amp; Mechatronic, or Sustainability engineering. In addition, students can specialise by selecting a Minor from a wide range of recommended subject sets that will complement their chosen discipline.\n                </div>\n</div>\n<div class="cmp-course__overview__two-column--right">\n<!-- Video-->\n<div class="video">\n<div class="video-component section component">\n<div class="video__contents">\n<iframe class="video__frame" data-gtm-yt-inspected-1666280_193="true" data-gtm-yt-inspected-46="true" id="widget2" src="https://www.youtube.com/embed/sOMg6-G19cI?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.westernsydney.edu.au" title="The Official Launch of Western Sydney Solar Teams Unlimited 5.0">\n</iframe>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>',
