    course_description = '<div class="cmp-course__overview__two-column">\n<div class="cmp-course__overview__two-column--container__wrapper">\n<div class="cmp-course__overview__two-column--left">\n<!---Title-->\n<div class="component component--tile">\n<div class="component component--title">\n<p style="font-weight: bold;" class="title__text">Project Management at Western</p>\n</div>\n</div>\n<!--- RTE component -->\n<div class="component component--wysiwyg">\n                    Our graduates are proficient in domain-specific customising of a project management life cycle and in developing comprehensive project plans involving cost and quality control, managing risks and stakeholders. You will also develop efficient schedules while being able to act as an effective communicator.\n\nSince project management occurs in a team setting, our project managers are taught to master team leadership and to understand team dynamics and behaviour in different team settings.\n                </div>\n</div>\n<div class="cmp-course__overview__two-column--right">\n<!-- Video-->\n<div class="video">\n<div class="video-component section component">\n<div class="video__contents">\n<iframe class="video__frame" data-gtm-yt-inspected-1666280_193="true" data-gtm-yt-inspected-46="true" id="widget2" src="https://www.youtube.com/embed/JFRPMWcCInI?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.westernsydney.edu.au" title="Why study Master of Project Management at Western">\n</iframe>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>',
