UPDATE courses SET
    course_description = '<div class="cmp-course__overview__two-column">\n<div class="cmp-course__overview__two-column--container__wrapper">\n<div class="cmp-course__overview__two-column--left">\n<!---Title-->\n<div class="component component--tile">\n<div class="component component--title">\n<p style="font-weight: bold;" class="title__text">Arts and Social Science at Western</p>\n</div>\n</div>\n<!--- RTE component -->\n<div class="component component--wysiwyg">\n                    This double degree program is designed for students whose interests span the Arts and Social Sciences.\n\nIt will give you the skills and versatility to work across a range of academic and professional disciplines, including the opportunity to develop global perspectives and communication skills.\n\nYou need to include a Social Science Major, so you can design your own academic program within the Bachelor of Social Science program structure.\n\nThis qualification in Social Science is then combined with an Arts major.\n                </div>\n</div>\n<div class="cmp-course__overview__two-column--right">\n<!-- Video-->\n<div class="video">\n<div class="video-component section component">\n<div class="video__contents">\n<iframe class="video__frame" data-gtm-yt-inspected-1666280_193="true" data-gtm-yt-inspected-46="true" id="widget2" src="https://www.youtube.com/embed/2Fy7Palbq4E?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.westernsydney.edu.au" title="Maddi: Bachelor of Social Science Student">\n</iframe>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>',
